import React, {useState} from 'react';
import {OfferPropType} from '../propertyGallery/propertyGallery';
import {OfferType} from '../../types/types';
import {useAppSelector} from '../../store/hooks';
import {useNavigate} from 'react-router-dom';
import {AppRoutes} from '../../routes/AppRoutes';
import {
  useFetchAddCommentMutation,
  useFetchRemoveFavoriteMutation,
  useGetFavoritesQuery
} from '../../features/sixCitiesApi';

// {/*place-card__bookmark-button--active*/}
type BookmarkTypeProp = {
  offer: OfferType;
};

function BookmarkMain({offer}: BookmarkTypeProp) {
  const auth = useAppSelector((state) => state.SERVER_DATA.auth);
  const navigate = useNavigate();
  const {data: favorites} = useGetFavoritesQuery();
  const isOfferFavorite = favorites?.includes(offer);
  const [fav, isFav] = useState(isOfferFavorite);
  const [fetchRemove] = useFetchRemoveFavoriteMutation();
  const [fetchAdd] = useFetchAddCommentMutation();

  const changer = () => {
    if (fav) {
      fetchRemove;
    } else {
      fetchAdd;
    }
  }

  return auth === 'AUTH'
    ? (
      <button
        className={fav ? 'place-card__bookmark-button button place-card__bookmark-button--active' : 'place-card__bookmark-button button' }
        type='button'
        onClick={changer}>
        <svg className='place-card__bookmark-icon' width='18' height='19'>
          <use xlinkHref='#icon-bookmark'></use>
        </svg>
        <span className='visually-hidden'>To bookmarks</span>
      </button>
      ) : (
      <button
        className={fav ? 'place-card__bookmark-button button place-card__bookmark-button--active' : 'place-card__bookmark-button button' }
        type='button'
        onClick={() => navigate(AppRoutes.Login)}
      >
        <svg className='place-card__bookmark-icon' width='18' height='19'>
          <use xlinkHref='#icon-bookmark'></use>
        </svg>
        <span className='visually-hidden'>To bookmarks</span>
      </button>
    );
}

export default BookmarkMain;
